import React, { useContext, useEffect, useState } from 'react';
import { NavLink, useLocation, useHistory } from 'react-router-dom';

import { MultiLangContext } from '../../context/MultiLang/MultiLang';

import './Search.scss';
import { classModifier } from "../../utils";
import { ROUTES } from "../../config/constants";
import API from "../../api/api";


const Search = () => {
  const location = useLocation();
  const history = useHistory();
  const {words} = useContext(MultiLangContext);

  const [searchState, setSearchState] = useState<{
    value: string | number;
    link: string;
  }>({
    value: '',
    link: '#',
  });

  useEffect(() => {

    console.log(searchState.value)

    // if (value.length === 64) {
    //   link = ROUTES.setTxRoute(value)
    // } else if (value.length === 40) {
    //   link = ROUTES.setAddressRoute(value)
    // } else {
    //   link = ROUTES.setBlockRoute(value)
    // }

    // API.getTransaction(match?.params.id)
    //   .then(res => {
    //     // console.log(res.data)
    //
    //     setTxState(res.data);
    //     setLoading(true);
    //   })
    //   .catch(err => {
    //     console.log(err)
    //
    //     history.push('/not-found')
    //   });
  }, [searchState.value]);

  function handleChange(e: any) {
    const value = e.target.value.split(' ').join('');

    // const link = () => { // TODO find work solution with switch/case
    //   switch (value.length) {
    //     case 64:
    //       return ROUTES.setTxRoute(value);
    //     case 40:
    //       return ROUTES.setAddressRoute(value)
    //     default:
    //       return ROUTES.setBlockRoute(value)
    //   }
    // }

    let link = ``;
    if (value.length === 64) {
      link = ROUTES.setTxRoute(value)
    } else if (value.length === 40) {
      link = ROUTES.setAddressRoute(value)
    } else {
      link = ROUTES.setBlockRoute(value)
    }

    setSearchState(prev => ({
      ...prev,
      value,
      link,
    }));
  }

  function handleKeyPress(e: any) {
    if (e.key === 'Enter') {
      history.push(searchState.link)
    }
  }


  return (
    <div className={classModifier('search',
      [(location.pathname === '/') ? 'main' : 'all'],
    )}>
      <div className="search__container">
        <div className="search-input__container">
          <input
            className='search-input'
            type="text"
            onChange={handleChange}
            onKeyPress={handleKeyPress}
            value={searchState.value}
            placeholder={words.searchPlaceholder}
          />

          <NavLink
            to={searchState.link}
            className='search-input__button'>
            <span>{words.find}</span>
          </NavLink>

        </div>
      </div>
    </div>
  );
}

export default Search;
