import React, { useContext, useEffect, useState } from 'react';
import { NavLink, useHistory, useRouteMatch } from 'react-router-dom';

import Moment from 'react-moment';

import API from '../../api/api';
import { MultiLangContext } from "../../context/MultiLang/MultiLang";
import { ROUTES } from "../../config/constants";

import { IMatchParams } from "../../interfaces/interfaces";

import Spinner from '../../components/Spinner';
import CopyItem from "../../components/CopyItem/CopyItem";
import './BlockPage.scss';


const BlockPage = () => {
  const history = useHistory();
  const {words, selectedLang} = useContext(MultiLangContext);
  const match = useRouteMatch<IMatchParams>('/block/:id');

  const [loading, setLoading] = useState(false);
  const [blockState, setBlockState] = useState<{
      hash: string;
      height: number;
      previousHash: string;
      timestamp: number;
      txHashes: any;
  }>({
      hash: '',
      height: 0,
      previousHash: '',
      timestamp: 0,
      txHashes: [],
  });

  useEffect(() => {
    setLoading(false);

    API.getBlock(match?.params.id)
      .then(res => {
        // console.log(res.data)

        setBlockState(prevState => ({
          ...prevState,
          ...res.data,
        }));
        setLoading(true);
      })
      .catch(err => {
        console.log(err);

        history.push('/not-found')
      });
    // eslint-disable-next-line
  }, [history, match?.params.id]);



  return (
    <>
      <section className="section section-block">
        <div className="container">
          {!loading
            ? (
              <Spinner/>
            )
            : (
              <>
                <h2 className="title">{words.blockDetails}</h2>

                <div className="details__block">
                  <div className="detail">
                    <h6 className="detail__name">{words.blockHash}:</h6>

                    <div className="copy__block">
                      <span className='text break-all copy__text'>{blockState.hash}</span>

                      <CopyItem
                        name={words.hash}
                        value={blockState.hash}
                      />
                    </div>
                  </div>

                  <div className="detail">
                    <h6 className="detail__name">{words.blockNumber}:</h6>

                    <span className="text">
                    {blockState.height}
                  </span>
                  </div>

                  <div className="detail">
                    <h6 className="detail__name">{words.timestamp}:</h6>

                    <span className="text">
                      <Moment date={blockState.timestamp}
                              utc
                              locale={selectedLang}
                              fromNow
                      />

                      <Moment date={blockState.timestamp}
                              utc
                              locale={selectedLang}
                              format=" (MMMM-DD-YYYY HH:mm:ss +UTC)"
                      />
                    </span>
                  </div>

                  <div className="detail">
                    <h6 className="detail__name">{words.txCount}:</h6>

                    <span className="text">
                    {blockState.txHashes.length}
                  </span>
                  </div>
                </div>

                <h2 className="title">{words.transactions}</h2>

                <div className="transaction-links">
                  {
                    blockState.txHashes.map((trans: string, index: number) => {
                      return (
                        <NavLink key={index}
                                 to={ROUTES.setTxRoute(trans)}>
                          <span className="text text--700">{index + 1}.</span>

                          <div className='transaction-links__content'>
                            <span className="text transaction-links__hash">
                              {trans}
                            </span>

                            <div className='transaction-links__icon'>
                              <svg width="18" height="18" viewBox="0 0 18 18" fill="none"
                                   xmlns="http://www.w3.org/2000/svg">
                                <path
                                  d="M7.5 9.75001C7.82209 10.1806 8.23302 10.5369 8.70491 10.7947C9.17681 11.0525 9.69863 11.2058 10.235 11.2443C10.7713 11.2827 11.3097 11.2053 11.8135 11.0173C12.3173 10.8294 12.7748 10.5353 13.155 10.155L15.405 7.90501C16.0881 7.19775 16.4661 6.25049 16.4575 5.26725C16.449 4.28401 16.0546 3.34347 15.3593 2.64819C14.664 1.9529 13.7235 1.55852 12.7403 1.54998C11.757 1.54143 10.8098 1.91941 10.1025 2.6025L8.8125 3.885"
                                  stroke="#AFB2C1" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                                <path
                                  d="M10.5 8.24999C10.1779 7.81939 9.76698 7.4631 9.29509 7.20528C8.82319 6.94746 8.30137 6.79415 7.76501 6.75574C7.22865 6.71732 6.69031 6.79471 6.18649 6.98265C5.68267 7.17059 5.22516 7.46468 4.845 7.84499L2.595 10.095C1.9119 10.8022 1.53393 11.7495 1.54247 12.7327C1.55101 13.716 1.9454 14.6565 2.64068 15.3518C3.33596 16.0471 4.27651 16.4415 5.25974 16.45C6.24298 16.4586 7.19024 16.0806 7.8975 15.3975L9.18 14.115"
                                  stroke="#AFB2C1" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                              </svg>
                            </div>
                          </div>
                        </NavLink>
                      )
                    })
                  }
                </div>

                <NavLink className='button button--home'
                         to={ROUTES.main}>
                  <span>{words.backToMain}</span>
                </NavLink>
              </>
            )
          }
        </div>
      </section>
    </>
  );
}

export default BlockPage;
